import React from 'react';
import './App.css';
import Controlled from './components/Controlled';
import UnControlled from './components/UnControlled';

function App() {
  return (
    <div className="App">
      <h1>Controlled vs Uncontrolled Components</h1>
      <hr />
      <div className="section">
        <h2>Controlled Component</h2>
        <p>The input value is controlled by React. It utilizes the onChange() method to update state.</p>
        <Controlled />
      </div>
      <hr />
      <div className="section">
        <h2>Uncontrolled Component</h2>
        <p>The input value is handled by the DOM itself. React does not control its value directly.</p>
        <UnControlled />
      </div>
      <hr />
    </div>
  );
}

export default App;
