import React, { useState, useEffect } from 'react';
import './App.css';
import Controlled from './components/Controlled';
import UnControlled from './components/UnControlled';

function App() {
  const [backgroundColor, setBackgroundColor] = useState("#ffffff");
  const [controlledValue, setControlledValue] = useState('');
  const [uncontrolledValue, setUncontrolledValue] = useState('');
  const [currentDateTime, setCurrentDateTime] = useState(new Date().toLocaleString());
  const [errorMessage, setErrorMessage] = useState('');

  const changeBackgroundColor = () => {
    const randomColor = '#' + Math.floor(Math.random() * 16777215).toString(16);
    setBackgroundColor(randomColor);
  };

  const reset = () => {
    setBackgroundColor("#ffffff");
    setControlledValue('');
    setUncontrolledValue('');
    setErrorMessage('');
  };

  const handleControlledChange = (value) => {
    setControlledValue(value);
    setErrorMessage('');
  };

  const handleUnControlledChange = (value) => {
    setUncontrolledValue(value);
  };

  const formatText = (text) => {
    // Mengubah teks menjadi Markdown untuk pemformatan sederhana
    return text.replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>").replace(/\*(.*?)\*/g, "<em>$1</em>");
  };

  const validateInput = (value) => {
    // Validasi sederhana untuk memastikan input tidak kosong
    return value.trim() !== ''; // Memeriksa apakah input tidak hanya berisi spasi
  };

  useEffect(() => {
    // Update waktu setiap detik
    const intervalId = setInterval(() => {
      setCurrentDateTime(new Date().toLocaleString());
    }, 1000);

    return () => clearInterval(intervalId); // Membersihkan interval saat komponen di-unmount
  }, []);

  const handleSubmit = () => {
    if (!validateInput(controlledValue)) {
      setErrorMessage('Input tidak boleh kosong!');
    } else {
      // Lakukan sesuatu dengan nilai terkendali yang valid
      alert('Input valid: ' + controlledValue);
    }
  };

  return (
    <div className="App" style={{ backgroundColor: backgroundColor }}>
      <h1>Selamat Datang di Dunia Tugas PSW dan Mahasiswa ^-^</h1>
      <p>Tanggal dan Waktu Saat Ini: {currentDateTime}</p>
      <hr />
      <div className="section">
        <h2>Tugas Terkendali</h2>
        <p>Lihatlah bagaimana usaha kami mengerjakan tugas-tugas ini!</p>
        <Controlled value={controlledValue} onChange={handleControlledChange} />
        <span style={{ color: 'red' }}>{errorMessage}</span>
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <hr />
      <div className="section">
        <h2>Pikiran Mahasiswa Tak Terkendali</h2>
        <p>Mahasiswa melakukan aktivitasnya dengan perjuangan keras!</p>
        <UnControlled value={uncontrolledValue} onChange={handleUnControlledChange} />
      </div>
      <hr />
      <button onClick={changeBackgroundColor}>Ubah Warna Latar</button>
      <button onClick={reset}>Reset</button>
    </div>
  );
}

export default App;
