import React, { useState } from 'react';
import './App.css';
import Controlled from './components/Controlled';
import UnControlled from './components/UnControlled';

function App() {
  const [backgroundColor, setBackgroundColor] = useState("#ffffff");
  const [clickCount, setClickCount] = useState(0);
  const [inputText, setInputText] = useState('');
  const [charCount, setCharCount] = useState(0);

  const changeBackgroundColor = () => {
    const randomColor = '#' + Math.floor(Math.random() * 16777215).toString(16);
    setBackgroundColor(randomColor);
    setClickCount(clickCount + 1); // Menambah jumlah klik setiap kali tombol random ditekan
  };

  const incrementClickCount = () => {
    setClickCount(clickCount + 1);
  };

  const handleInputChange = (event) => {
    const text = event.target.value;
    setInputText(text);
    setCharCount(text.length);
  };

  const reset = () => {
    setBackgroundColor("#ffffff");
    setClickCount(0);
    setInputText('');
    setCharCount(0);
  };

  return (
    <div className="App" style={{ backgroundColor: backgroundColor }}>
      <h1>Selamat Datang di Dunia Tugas PSW dan Mahasiswa ^-^</h1>
      <hr />
      <div className="section">
        <h2>Tugas Terkendali</h2>
        <p>Lihatlah bagaimana usaha kami mengerjakan tugas-tugas ini!</p>
        <Controlled />
      </div>
      <hr />
      <div className="section">
        <h2>Pikiran Mahasiswa Tak Terkendali</h2>
        <p>Mahasiswa melakukan aktivitasnya dengan perjuangan keras!</p>
        <UnControlled />
      </div>
      <hr />
      <button onClick={changeBackgroundColor}>Ubah Warna Latar</button>
      <button onClick={incrementClickCount}>Klik saya! ({clickCount})</button>
      <div className="input-section">
        <input type="text" value={inputText} onChange={handleInputChange} placeholder="Masukkan teks" />
        <p>Jumlah karakter: {charCount}</p>
      </div>
      <button onClick={reset}>Reset</button>
    </div>
  );
}

export default App;
