import React, { useState } from 'react';
import './App.css';
import Controlled from './components/Controlled';
import UnControlled from './components/UnControlled';

function App() {
  const [backgroundColor, setBackgroundColor] = useState("#ffffff");
  const [controlledValue, setControlledValue] = useState('');
  const [uncontrolledValue, setUncontrolledValue] = useState('');
  const [totalCharacters, setTotalCharacters] = useState(0);

  const changeBackgroundColor = () => {
    const randomColor = '#' + Math.floor(Math.random() * 16777215).toString(16);
    setBackgroundColor(randomColor);
  };

  const reset = () => {
    setBackgroundColor("#ffffff");
    setControlledValue('');
    setUncontrolledValue('');
    setTotalCharacters(0);
  };

  const handleControlledChange = (value) => {
    setControlledValue(value);
    updateTotalCharacters(value, uncontrolledValue);
  };

  const handleUnControlledChange = (value) => {
    setUncontrolledValue(value);
    updateTotalCharacters(controlledValue, value);
  };

  const updateTotalCharacters = (controlledText, uncontrolledText) => {
    const total = controlledText.length + uncontrolledText.length;
    setTotalCharacters(total);
  };

  return (
    <div className="App" style={{ backgroundColor: backgroundColor }}>
      <h1>Selamat Datang di Dunia Tugas PSW dan Mahasiswa ^-^</h1>
      <hr />
      <div className="section">
        <h2>Tugas Terkendali</h2>
        <p>Lihatlah bagaimana usaha kami mengerjakan tugas-tugas ini!</p>
        <Controlled value={controlledValue} onChange={handleControlledChange} />
        <p>Jumlah karakter: {controlledValue.length}</p>
      </div>
      <hr />
      <div className="section">
        <h2>Pikiran Mahasiswa Tak Terkendali</h2>
        <p>Mahasiswa melakukan aktivitasnya dengan perjuangan keras!</p>
        <UnControlled value={uncontrolledValue} onChange={handleUnControlledChange} />
        <p>Jumlah karakter: {uncontrolledValue.length}</p>
      </div>
      <hr />
      <p>Total karakter dari kedua komponen: {totalCharacters}</p>
      <hr />
      <button onClick={changeBackgroundColor}>Ubah Warna Latar</button>
      <button onClick={reset}>Reset</button>
    </div>
  );
}

export default App;
